functions.previewShader:
highp float d = distance(uv.xy, vec2(0.5, 0.5));
d += sin(d + time) * 0.01;
highp float a = abs(d - 0.4) - 0.02;
highp float b = abs(d - 0.3) - 0.02;
highp float c = abs(d - 0.2) - 0.02;
highp float all = max(max(a, b), c);

color = vec3(step(all, 0.0));

highp float t = sin(time) * 0.5 + 0.5;
highp vec2 coords = vec2(lerp(0.25,0.75, t)); 
highp float circleDF = length(coords) - 0.2;
highp float squareDF = min(abs(coords.x - 0.2), abs(coords.y - 0.2));
highp float shape = mix(circleDF, squareDF, t);
color = vec3(shape);

highp vec2 b = vec2(0.5 + sin(time) * 0.15, 0.5);
highp float c = step(distance(uv.xy, b), 0.3);
if (uv.x < 0.5)
	color = vec3(c);
else
	color = vec3(1.0 - c);
