highp float angle = time;
highp vec2 rotUv = uv - vec2(0.5, 0.5);
rotUv = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * rotUv;

highp float crossA = max(abs(rotUv.x + 0.5) - 0.25, abs(rotUv.y) - 0.02);

highp float d = distance(uv.xy, vec2(0.5, 0.5));
d += sin(d * 8.0 + time * 4.0) * 0.02;
highp float a = abs(d - 0.4) - 0.02;
highp float b = abs(d - 0.3) - 0.02;
highp float c = abs(d - 0.2) - 0.02;
highp float all = min(min(a, b), c);

color = vec3(smoothstep(0.01, 0.0, all));

highp float d = distance(uv.xy, vec2(0.5));
highp float a = abs(d - 0.25) - 0.1;

highp vec2 dir = normalize(uv.xy - vec2(0.5));
highp float angle = time * 5.0;
highp vec2 targetDir = vec2(sin(angle), cos(angle));
highp float align = dot(dir, targetDir) * 0.5 + 0.5;

color = vec3(smoothstep(0.01, 0.0, a) * align);

highp float t = sin(time) * 0.5 + 0.5;
highp vec2 coords = vec2(mix(0.25, 0.75, t)); 
highp float circleDF = distance(uv, coords) - 0.2;
highp vec2 square = abs(uv - coords) - vec2(0.2);
highp float squareDF = max(square.x, square.y);
highp float shape = smoothstep(0.01, 0.0, mix(circleDF, squareDF, t));
color = vec3(shape);
//color = vec3(circleDF, squareDF, t);

highp float minusShape = max(abs(uv.x - 0.27) - 0.1, abs(uv.y - 0.5) - 0.05);

highp float angle = 3.1415 * 0.25;
highp vec2 rotUv = uv - vec2(0.65, 0.5);
rotUv = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * rotUv;

highp float crossA = max(abs(rotUv.x) - 0.25, abs(rotUv.y) - 0.05);
highp float crossB = max(abs(rotUv.x) - 0.05, abs(rotUv.y) - 0.25);

highp float shape = min(min(minusShape, crossA), crossB);

color = vec3(step(shape , 0.0));

highp vec2 dir = uv - vec2(0.5);
highp float dist = length(dir);
highp vec2 normDir = dir / dist;
highp float angle = atan(dir.x, dir.y);
highp float section = floor(angle / 6.2831853 * 20.0);

highp float rand2 = sin(section * 917.3551) * 0.5 + 0.5;
highp float sectRand = sin(section * 96.2457 + rand2) * 0.5 + 0.5;
sectRand = sectRand * 0.3 + 0.2;

highp vec2 targetDir = vec2(sin(time), cos(time));
highp float align = dot(normDir, targetDir) * 0.5 + 0.5;
align = smoothstep(0.6, 1.0, align);
sectRand = mix(sectRand, 0.4, sin(time) * 0.5 + 0.5);

highp float disc = step(length(dir), 0.4);
highp float sectRange = step(dist, sectRand);
color = vec3(sectRange);

highp vec2 b = vec2(0.5 + sin(time) * 0.15, 0.5);
highp float c = smoothstep(0.01, 0.0, distance(uv.xy, b) - 0.3);
if (uv.x < 0.5)
	color = vec3(c);
else
	color = vec3(1.0 - c);
