indexShader:
highp float t = sin(time) * 0.5 + 0.5;
highp vec2 coords = vec2(mix(0.25, 0.75, t)); 
highp float circleDF = length(coords) - 0.2;
highp float squareDF = min(abs(coords.x - 0.2), abs(coords.y - 0.2));
highp float shape = step(0.0, mix(circleDF, squareDF, t));
color = vec3(circleDF, squareDF, t);
