indexShader:
highp float minusShape = max(abs(uv.x - 0.4) - 0.15, abs(uv.y - 0.5) - 0.05);

highp float angle = 3.1415 * 0.25;
highp vec2 rotUv = uv - vec2(0.5);
rotUv = mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * rotUv;

highp float crossA = max(abs(rotUv.x + 0.1) - 0.2, abs(rotUv.y) - 0.05);
highp float crossB = max(abs(rotUv.x + 0.1) - 0.05, abs(rotUv.y) - 0.2);

color = vec3(step(minusShape, 0.0), step(crossA, 0.0), step(crossB, 0.0));
