indexShader:
highp vec2 dir = uv - vec2(0.5);
highp float dist = length(dir);
highp vec2 normDir = dir / dist;
highp float angle = atan(dir.x, dir.y);
highp float section = floor(angle / 6.2831853 * 10.0);
highp float rand2 = sin(section * 917.3551) * 0.5 + 0.5;
highp float sectRand = sin(section * 96.2457 + rand2 + time * 2.0) * 0.5 + 0.5;
sectRand = sectRand * 0.3 + 0.2;

highp vec2 targetDir = vec2(sin(angle), cos(angle));
highp float align = dot(normDir, targetDir) * 0.5 + 0.5;
//align = smoothstep(0.6, 1.0, align);

sectRand = mix(sectRand, 0.4, align);

highp float disc = step(length(dir), 0.4);
highp float sectRange = step(dist, sectRand);
color = vec3(disc, sectRange, disc * sectRange);
